import Box from "@/components/Layout/Box";
import Slider from "@/components/Slider/Slider";
import Card from "@/components/Surfaces/Card";
import Typography from "@/components/Typography/Typography";
import { theme } from "@/styles/theme";
import Progress from "@/components/Feedback/ProgressBar";
import { useTranslation } from "react-i18next";
import Icon_1 from "@assets/images/react_icon.svg";
import Icon_2 from "@assets/images/typescript_icon.svg";
import Icon_3 from "@assets/images/nextjs_icon.svg";
import Icon_4 from "@assets/images/html5_icon.svg";
import Icon_5 from "@assets/images/css3_icon.svg";
import Icon_6 from "@assets/images/javascript_icon.svg";

const TechnologyStackSection = () => {
  const { t } = useTranslation();

  const cardItems = [
    {
      title: t("TECHNOLOGY_CARD_REACT_TITLE"),
      value: 90,
      image: Icon_1,
    },
    {
      title: t("TECHNOLOGY_CARD_NEXT_TITLE"),
      value: 70,
      image: Icon_2,
    },
    {
      title: t("TECHNOLOGY_CARD_TYPESCRIPT_TITLE"),
      value: 95,
      image: Icon_3,
    },
    {
      title: t("TECHNOLOGY_CARD_HTML_TITLE"),
      value: 98,
      image: Icon_4,
    },
    {
      title: t("TECHNOLOGY_CARD_CSS_TITLE"),
      value: 97,
      image: Icon_5,
    },
    {
      title: t("TECHNOLOGY_CARD_JAVASCRIPT_TITLE"),
      value: 90,
      image: Icon_6,
    },
  ];

  return (
    <>
      <Box
        $alignItems='flex-start'
        $justifyContent='flex-start'
        $sx={{ marginInlineStart: "calc(-268.46154px + 28.36538vw)" }}
      >
        <Typography
          $variant='h2'
          $marginBottom='0.7%'
          $color={theme.colors.primary.main}
          $marginRight='10px'
        >
          {t("TECHNOLOGY_TITLE")}
        </Typography>
        <Typography $variant='h2' $marginBottom='3%' $textAlign='center'>
          {t("TECHNOLOGY_TEXT")}
        </Typography>
      </Box>
      <Slider
        loop={false}
        items={cardItems.map((item, index) => (
          <Card key={index} $isHover={false}>
            <Box $height='48px'>
              <img src={item.image} alt='로고 이미지' />
            </Box>
            <Typography
              $variant='h3'
              $marginBottom='5%'
              $marginTop='8%'
              $textAlign='left'
              $fontFamily='en'
            >
              {item.title}
            </Typography>
            <Progress value={item.value} />
          </Card>
        ))}
      />
    </>
  );
};

export default TechnologyStackSection;
