import { Suspense, lazy } from "react";
import Button from "@/components/Button/Button";
import Box from "@/components/Layout/Box";
import Typography from "@/components/Typography/Typography";
import FileDownloadIcon from "@mui/icons-material/FileDownload";
import { theme } from "@/styles/theme";
import { useTranslation } from "react-i18next";
import file from "@assets/files/GaYoung_CV.pdf";

/**---------------------------------------------------------------------------/
 * ! BackGroundSection
 * * 메인 간단한 자기소개 영역
/**--------------------------------------------------------------------------*/

const BackGroundSection = () => {
  const { t } = useTranslation();
  const Skeleton = lazy(async () => {
    await new Promise((resolve) => setTimeout(resolve, 2000)); // 2초 강제 지연
    return import("@/components/Feedback/Skeleton");
  });

  return (
    <Box $flexDirection='column' $alignItems='center'>
      <Suspense
        fallback={<Skeleton $variant='text' $width='60%' $height='1rem' />}
      >
        <Typography $variant='h6' $marginBottom='1%'>
          {t("BACKGROUND_SECTION_MESSAGE_CAPTION")}
        </Typography>
      </Suspense>
      <Suspense
        fallback={<Skeleton $variant='text' $width='80%' $height='2rem' />}
      >
        <Typography $variant='h2' $marginBottom='3%' $textAlign='center'>
          {t("BACKGROUND_SECTION_MESSAGE_TITLE")}
        </Typography>
      </Suspense>
      <Suspense
        fallback={<Skeleton $variant='text' $width='90%' $height='1.5rem' />}
      >
        <Typography
          $variant='p'
          $color={theme.colors.grey[700]}
          $textAlign='center'
        >
          {t("BACKGROUND_SECTION_MESSAGE_TEXT")}
        </Typography>
      </Suspense>
      <Suspense
        fallback={
          <Skeleton $variant='rectangular' $width='150px' $height='50px' />
        }
      >
        <Button
          startIcon={<FileDownloadIcon />}
          $marginTop='5%'
          $backgroundColor={theme.colors.grey[800]}
          $fileName='GaYoung_CV'
          $fileUrl={file}
        >
          {t("DOWNLOAD_CV")}
        </Button>
      </Suspense>
    </Box>
  );
};

export default BackGroundSection;
