import { theme } from "@/styles/theme";
import { ReactNode, useEffect, useState } from "react";
import { css, styled } from "styled-components";
import ErrorOutlineIcon from "@mui/icons-material/ErrorOutline";
import WarningAmberIcon from "@mui/icons-material/WarningAmber";
import InfoIcon from "@mui/icons-material/Info";
import CheckCircleOutlineIcon from "@mui/icons-material/CheckCircleOutline";
import IconButton from "@/components/Button/IconButton";
import HighlightOffIcon from "@mui/icons-material/HighlightOff";
import Typography from "@/components/Typography/Typography";
import { zIndexAlert } from "@/styles/zIndex";

export type AlertProps = {
  message: ReactNode;
  severity?: "error" | "warning" | "info" | "success";
  autoCloseDuration?: number;
};

const Alert = ({
  message,
  severity = "info",
  autoCloseDuration = 5000,
}: AlertProps) => {
  const [isClosed, setIsClosed] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsClosed(true);
    }, autoCloseDuration);

    return () => clearTimeout(timer);
  }, [autoCloseDuration]);

  if (isClosed) return null;

  return (
    <StyledAlert severity={severity}>
      <IconWrapper severity={severity}>
        {severity === "error" && <ErrorOutlineIcon />}
        {severity === "warning" && <WarningAmberIcon />}
        {severity === "info" && <InfoIcon />}
        {severity === "success" && <CheckCircleOutlineIcon />}
      </IconWrapper>
      <Typography $variant='h6'>{message}</Typography>

      <IconButton
        onClick={() => setIsClosed(true)}
        isBackground={false}
        isHover={false}
        $sx={{ padding: 0 }}
      >
        <HighlightOffIcon />
      </IconButton>
    </StyledAlert>
  );
};

// Alert들을 한 곳에서 관리하는 AlertWrapper
const AlertWrapper = ({ alerts }: { alerts: AlertProps[] }) => {
  return (
    <Wrapper>
      {alerts.map((alert, index) => (
        <Alert key={index} {...alert} />
      ))}
    </Wrapper>
  );
};

const Wrapper = styled.div`
  position: fixed;
  top: 10px;
  left: 50%;
  transform: translateX(-50%);
  width: 400px;
  z-index: ${zIndexAlert};
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const StyledAlert = styled.div<{ severity: string }>`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: ${({ severity }) => theme.colors.validation[severity]};
  color: ${theme.colors.white};
  border-radius: ${theme.borderRadius[4]};
  padding: 10px 16px;
  width: 100%;
`;

const IconWrapper = styled.span<{ severity: string }>`
  margin-right: 12px;
  font-size: 24px;
  color: ${theme.colors.white};
`;

export default AlertWrapper;
