import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import translationData from "../locales/translations.json"; // ✅ 상대 경로로 변경해서 테스트!

console.log("📜 번역 데이터 확인:", translationData);

i18n
  .use(initReactI18next) // React에서 사용 가능하게 설정
  .init({
    resources: translationData, // JSON 번역 데이터 사용
    lng: "ko", // 기본 언어 (필요에 따라 변경 가능)
    fallbackLng: "en", // 설정된 언어가 없을 때 기본 언어
    interpolation: {
      escapeValue: false, // XSS 방지를 위해 escape 비활성화
    },
  });

export default i18n;
