import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const loadTranslations = async () => {
  const response = await fetch("/locales/translations.json"); // ✅ 서버에서 JSON 가져오기
  const data = await response.json();
  return data;
};

loadTranslations().then((translationData) => {
  i18n.use(initReactI18next).init({
    resources: translationData, // ✅ 데이터를 동적으로 로드!
    lng: "ko",
    fallbackLng: "en",
    interpolation: { escapeValue: false },
  });
});

export default i18n;
