import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import translationData from "@locales/translations.json";

i18n.use(initReactI18next).init({
  resources: translationData,
  lng: "ko",
  fallbackLng: "en",
  interpolation: {
    escapeValue: false,
  },
});

console.log("üåç Ï¥àÍ∏∞ ÏÑ§Ï†ï Ïñ∏Ïñ¥:", i18n.language);
console.log(
  "üî§ Î≤àÏó≠ Îç∞Ïù¥ÌÑ∞ ÌôïÏù∏ (MAIN_MESSAGE_TITLE):?????????????????????????????????????",
  i18n.t("MAIN_MESSAGE_TITLE")
);

export default i18n;
