import Box from "@/components/Layout/Box";
import Slider from "@/components/slider/Slider";
import Card from "@/components/Surfaces/Card";
import Typography from "@/components/Typography/Typography";
import { DEVELOPMENT_TEXT, DEVELOPMENT_TITLE } from "@/constants/string";
import { theme } from "@/styles/theme";
import ProjectImg_1 from "@assets/images/sub_eventpage.png";
import ProjectImg_2 from "@assets/images/sub_loa.png";
import ProjectImg_3 from "@assets/images/sub_mbs.png";
import ProjectImg_4 from "@assets/images/sub_protoken.png";
import ProjectImg_5 from "@assets/images/sub_proup.png";

const TechnologyStackSection = () => {
  const cardItems = [
    {
      title: DEVELOPMENT_TEXT,
      description: DEVELOPMENT_TEXT,
      image: ProjectImg_1,
    },
    {
      title: DEVELOPMENT_TEXT,
      description: DEVELOPMENT_TEXT,
      image: ProjectImg_2,
    },
    {
      title: DEVELOPMENT_TEXT,
      description: DEVELOPMENT_TEXT,
      image: ProjectImg_3,
    },
    {
      title: DEVELOPMENT_TEXT,
      description: DEVELOPMENT_TEXT,
      image: ProjectImg_4,
    },
    {
      title: DEVELOPMENT_TEXT,
      description: DEVELOPMENT_TEXT,
      image: ProjectImg_5,
    },
  ];

  return (
    <>
      <Box
        $alignItems='flex-start'
        $justifyContent='flex-start'
        $sx={{ marginInlineStart: " calc(-268.46154px + 28.36538vw)" }}
      >
        <Typography
          $variant='h2'
          $marginBottom='0.7%'
          $color={theme.colors.primary.main}
          $marginRight='10px'
        >
          {DEVELOPMENT_TITLE}
        </Typography>
        <Typography $variant='h2' $marginBottom='3%' $textAlign='center'>
          {DEVELOPMENT_TEXT}
        </Typography>
      </Box>
      <Slider
        items={cardItems.map((item, index) => (
          <Card key={index}>
            <Typography
              $variant='h3'
              $marginBottom='5%'
              $marginTop='8%'
              $textAlign='left'
            >
              {item.title}
            </Typography>
            <Typography
              $variant='p'
              $marginBottom='15%'
              $marginTop='8%'
              $textAlign='left'
            >
              {item.description}
            </Typography>
            <img src={item.image} alt='프로젝트 이미지' />
          </Card>
        ))}
      />
    </>
  );
};

export default TechnologyStackSection;
