import styled, { css, keyframes } from "styled-components";

export type ProgressProps = {
  value: number; // 진행률 (0~100)
  variant?: "bar" | "circular"; // bar 또는 circular
  size?: number; // Circular 크기 (px)
  barWidth?: string; // Bar 너비 (ex: "300px", "100%")
  height?: string; // Bar 높이 (ex: "10px")
  color?: string; // 색상
};

const Progress = ({
  value,
  variant = "bar",
  size = 100,
  barWidth = "100%",
  height = "8px",
  color = "#007bff",
}: ProgressProps) => {
  return variant === "bar" ? (
    <BarContainer width={barWidth} height={height}>
      <BarFill value={value} color={color} />
    </BarContainer>
  ) : (
    <CircularContainer size={size}>
      <svg viewBox='0 0 100 100'>
        <circle className='bg' cx='50' cy='50' r='45' />
        <circle
          className='progress'
          cx='50'
          cy='50'
          r='45'
          strokeDasharray='283'
          strokeDashoffset={283 - (value / 100) * 283}
          style={{ stroke: color }}
        />
        <text x='50%' y='50%' dy='.3em' textAnchor='middle'>
          {value}%
        </text>
      </svg>
    </CircularContainer>
  );
};

const BarContainer = styled.div<{ width: string; height: string }>`
  width: ${(props) => props.width};
  height: ${(props) => props.height};
  background: #e5e9eb;
  border-radius: 4px;
  overflow: hidden;
  position: relative;
`;

const progressAnimation = keyframes`
  from { width: 0%; }
  to { width: 100%; }
`;

const BarFill = styled.div<{ value: number; color: string }>`
  height: 100%;
  width: ${(props) => props.value}%;
  background: ${(props) => props.color};
  border-radius: 4px;
  transition: width 1.5s ease-in-out;
  animation: ${progressAnimation} 1.5s ease-in-out;
`;

const CircularContainer = styled.div<{ size: number }>`
  width: ${(props) => props.size}px;
  height: ${(props) => props.size}px;
  position: relative;

  svg {
    transform: rotate(-90deg);
    width: 100%;
    height: 100%;
  }

  circle {
    fill: none;
    stroke-width: 10;
  }

  .bg {
    stroke: #e5e9eb;
  }

  .progress {
    stroke-linecap: round;
    transition: stroke-dashoffset 1.5s ease-in-out;
  }

  text {
    font-size: 18px;
    font-weight: bold;
    fill: #333;
  }
`;

export default Progress;
