import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import translationData from "@locales/translations.json"; // ✅ JSON 파일 불러오기

console.log("📜 번역 데이터 확인:", translationData); // ✅ JSON 데이터 확인!

i18n.use(initReactI18next).init({
  resources: {
    ko: { translation: translationData.ko }, // ✅ `translation` 키로 감싸기!
    en: { translation: translationData.en },
    ja: { translation: translationData.ja },
    fr: { translation: translationData.fr },
  },
  lng: "ko", // 기본 언어
  fallbackLng: "en", // 기본값
  interpolation: { escapeValue: false },
});

console.log("🌍 초기 설정 언어:", i18n.language);
console.log(
  "🔤 번역 데이터 확인 (MAIN_MESSAGE_TITLE):",
  i18n.t("MAIN_MESSAGE_TITLE")
);

export default i18n;
