import { styled } from "styled-components";
import { ReactNode } from "react";
/**---------------------------------------------------------------------------/
  * ! Main 컴포넌트
  * * 페이지의 메인 컨텐츠 영역을 담당하는 레이아웃
  * * 자식 컴포넌트 (`children`)를 받아서 표시
  * * `flex` 레이아웃을 사용하여 자식 요소들을 수직으로 배치
  * * `background-color`는 `theme.colors.background.light`로 설정하여 밝은 배경색을 사용
  * * `flex-grow: 1`로 상위 컨테이너에서 가능한 공간을 모두 차지하도록 설정
/**--------------------------------------------------------------------------*/

const Main = ({ children }: { children: ReactNode }) => (
  <MainStyle>{children}</MainStyle>
);

export const MainStyle = styled.main`
  width: 100%;
  height: auto;
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  background-color: ${({ theme }) => theme.colors.background.light};
`;

export default Main;
