import { ReactNode } from "react";
import { styled } from "styled-components";
import { theme } from "@/styles/theme";

export type AsideProps = {
  $backgroundColor?: string;
  $height?: string;
  children: ReactNode;
};
/**---------------------------------------------------------------------------/
  * ! Aside 컴포넌트
  * * 사이드바 레이아웃을 위한 컴포넌트
  * * `$backgroundColor`: 배경 색상 설정, 기본값은 `theme.colors.background.main`
  * * `$height`: 높이 설정, 기본값은 `"auto"`
  * * `children`: 사이드바 안에 포함될 내용 (ReactNode)
  * * flex 레이아웃을 사용하여 세로 방향으로 배치하며, 사이드바 영역에 적합한 스타일 제공
  * * 기본적으로 `aside` 요소로 렌더링
/**--------------------------------------------------------------------------*/

const Aside = (props: AsideProps) => {
  const {
    $backgroundColor = theme.colors.background.main,
    $height = "auto",
    children,
  } = props;
  return (
    <AsideStyle $backgroundColor={$backgroundColor} $height={$height}>
      {children}
    </AsideStyle>
  );
};

export const AsideStyle = styled.aside<{
  $backgroundColor: string;
  $height: string;
}>`
  width: 100%;
  height: ${(props) => props.$height};
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  padding: 50px 12px 12px 12px;
  background-color: ${(props) => props.$backgroundColor};
`;

export default Aside;
