import Box from "@/components/Layout/Box";
import Typography from "@/components/Typography/Typography";
import {
  BLOG_URL,
  GIT_HUB_URL,
  MAIN_MESSAGE_TEXT,
  MAIN_MESSAGE_TITLE,
  MAIN_TEXTS_ANIMATION_ARR,
} from "@/constants/string";
import { TextFlipAnimation } from "@/styles/animation";
import { theme } from "@/styles/theme";
import { styled } from "styled-components";
import gifImage from "@assets/images/emoji.gif";
import IconButton from "@/components/Button/IconButton";
import GitHubIcon from "@mui/icons-material/GitHub";
import WysiwygIcon from "@mui/icons-material/Wysiwyg";
/**---------------------------------------------------------------------------/

 * ! MainTextAnimationSection
 * * 메인 페이지 메인 부분

/**--------------------------------------------------------------------------*/
const MainTextAnimationSection = () => {
  return (
    <Box $height='100%' $alignItems='center' $justifyContent='space-around'>
      <Box $flexDirection='column' $width='auto'>
        <Typography
          $variant='h1'
          $color={theme.colors.grey[100]}
          $marginBottom='10px'
        >
          {MAIN_MESSAGE_TITLE}
        </Typography>
        <FlipWrap>
          <Flip>
            {MAIN_TEXTS_ANIMATION_ARR.map((text, index) => (
              <Typography
                key={index}
                $variant='h1'
                $color={theme.colors.grey[100]}
              >
                {text}
              </Typography>
            ))}
          </Flip>
          <Typography
            $variant='h1'
            $color={theme.colors.grey[100]}
            $marginTop='10px'
          >
            {MAIN_MESSAGE_TEXT}
          </Typography>
        </FlipWrap>
        <Box $marginTop='5%'>
          <IconButton
            href={GIT_HUB_URL}
            captionText='GitHub 바로 가기'
            $backgroundColor={theme.colors.background.light}
          >
            <GitHubIcon />
          </IconButton>
          <IconButton
            href={BLOG_URL}
            captionText='블로그 바로 가기'
            $backgroundColor={theme.colors.background.light}
          >
            <WysiwygIcon />
          </IconButton>
        </Box>
      </Box>

      <img src={gifImage} alt='gifImage' />
    </Box>
  );
};
export default MainTextAnimationSection;

const FlipWrap = styled.ul`
  position: relative;
`;

const Flip = styled.li`
  height: 50px;
  overflow: hidden;
  h1 {
    width: 100%;
    height: 100%;
    line-height: 45px;
  }
  h1:nth-child(1) {
    background-color: ${({ theme }) => theme.colors.primary.dark};
    animation: ${TextFlipAnimation} 5s linear infinite;
  }
  h1:nth-child(2) {
    background-color: ${({ theme }) => theme.colors.primary.light};
  }
  h1:nth-child(3) {
    background-color: ${({ theme }) => theme.colors.primary.main};
  }
`;
