import { ReactNode } from "react";
import styled from "styled-components";
import { Swiper, SwiperProps, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.css";
import { Mousewheel, Navigation } from "swiper/modules";
import "swiper/css/navigation";
import { FlashOffOutlined } from "@mui/icons-material";
// CardProps 타입 정의
export type CardProps = SwiperProps & {
  items: ReactNode[]; // items로 ReactNode 배열을 받음
  spaceBetween?: number; // 추가적인 Swiper 옵션 (슬라이드 간격 등)
  slidesPerView?: number; // 한 번에 보여줄 슬라이드 개수
  centeredSlides?: boolean;
  freeMode?: boolean;

  $sx?: Record<string, any>;
};

const Slider = (props: CardProps) => {
  const {
    items,
    spaceBetween = 20,
    slidesPerView = 4,
    centeredSlides = false,
    freeMode = true,
    loop = true,
  } = props;
  return (
    <SliderContainer>
      <Swiper
        spaceBetween={spaceBetween}
        slidesPerView={slidesPerView}
        navigation={true}
        modules={[, Mousewheel]}
        centeredSlides={centeredSlides}
        freeMode={freeMode}
        loop={loop}
        {...props}
      >
        {items.map((item, index) => (
          <SwiperSlide key={index}>{item}</SwiperSlide>
        ))}
      </Swiper>
    </SliderContainer>
  );
};

const SliderContainer = styled.div`
  width: 100%;

  .swiper-slide {
    display: flex;
    flex-direction: column;
    justify-content: center;
  }
  .swiper-wrapper {
    /* margin-left: 20%; */
    margin-inline-start: calc(-268.46154px + 28.36538vw);
  }
  /* max-width: 800px; */
`;

const SlideItem = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  /* background-color: #eee;
  border-radius: 8px;
  padding: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px; */
`;

export default Slider;
