import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import translations from "./constants/translations.json";

i18n.use(initReactI18next).init({
  resources: translations, // translations.json 파일을 리소스로 사용
  lng: "ko", // 기본 언어를 'ko'로 설정
  fallbackLng: "ko", // 로딩되지 않은 언어는 기본 'ko'를 사용
  interpolation: {
    escapeValue: false, // React는 자동으로 escaping 처리를 하므로 설정할 필요 없음
  },
});

export default i18n;
