import { theme } from "@/styles/theme";
import { ReactNode } from "react";
import { styled } from "styled-components";

export type SectionProps = {
  $backgroundColor?: string;
  $height?: string;
  children: ReactNode;
  $flexDirection?: string;
};
/**---------------------------------------------------------------------------/
  * ! Section 컴포넌트
  * * 페이지 내 특정 섹션을 위한 레이아웃 컨테이너
  * * `flex` 레이아웃을 사용하여 자식 요소를 배치
  * * `flexDirection`을 통해 자식 요소의 배치 방향을 설정 (기본값: `column`)
  * * `backgroundColor`와 `height`는 props로 전달 가능, 기본값으로 배경색과 높이가 설정됨
  * * `padding`을 사용하여 상하 여백을 설정 (`50px 0`)
  * * 자식 컴포넌트를 `children`으로 받아서 표시
/**--------------------------------------------------------------------------*/

const Section = (props: SectionProps) => {
  const {
    $backgroundColor = theme.colors.background.main,
    $height = "auto",
    children,
    $flexDirection = "column",
  } = props;

  return (
    <SectionStyle
      $backgroundColor={$backgroundColor}
      $height={$height}
      $flexDirection={$flexDirection}
    >
      {children}
    </SectionStyle>
  );
};

export const SectionStyle = styled.section<SectionProps>`
  width: 100%;
  height: ${({ $height }) => $height};
  overflow-x: hidden;
  display: flex;
  flex-direction: ${({ $flexDirection }) => $flexDirection};
  padding: 80px 0;
  background-color: ${({ $backgroundColor }) => $backgroundColor};
`;

export default Section;
