import { ReactNode } from "react";
import { styled, css } from "styled-components";
import { theme } from "@/styles/theme";
import { darken } from "@/utils/colorUtils";

export type IconButtonProps = {
  children: ReactNode;
  $color?: string;
  $backgroundColor?: string;
  disabled?: boolean;
  onClick?: () => void;
  isBackground?: boolean;
  isHover?: boolean;
  captionText?: string;
  href?: string;
  $sx?: Record<string, any>;
};

const IconButton = ({
  children,
  $color = theme.colors.grey[100],
  $backgroundColor = theme.colors.primary.main,
  disabled = false,
  onClick,
  isBackground = true,
  isHover = true,
  captionText,
  href,
  $sx = {},
}: IconButtonProps) => {
  const handleClick = () => {
    if (href) {
      window.open(href, "_blank", "새창 이동");
    } else if (onClick) {
      onClick();
    }
  };

  return (
    <ButtonStyle
      $color={$color}
      $backgroundColor={$backgroundColor}
      disabled={disabled}
      onClick={handleClick}
      $isBackground={isBackground}
      $isHover={isHover}
      $sx={$sx} // sx를 $sx로 전달
    >
      <IconWrapper $color={$color}>{children}</IconWrapper>
      {captionText && isHover && <Caption>{captionText}</Caption>}
    </ButtonStyle>
  );
};

const ButtonStyle = styled.button<{
  $color: string;
  $backgroundColor: string;
  disabled: boolean;
  $isBackground: boolean;
  $isHover: boolean;
  $sx?: Record<string, any>;
}>`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 6px;
  font-size: 1.75rem;
  font-weight: bold;
  margin: 5px;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  background-color: ${(props) =>
    props.$isBackground && !props.disabled
      ? props.$backgroundColor
      : "transparent"};
  color: ${(props) =>
    props.$isBackground ? props.$color : props.$backgroundColor};
  border-radius: 50%;
  position: relative;

  &:hover {
    background-color: ${(props) =>
      props.$isHover && !props.disabled
        ? props.$backgroundColor
          ? darken(props.$backgroundColor, 0.2)
          : theme.colors.primary.dark
        : "transparent"};
    color: ${(props) =>
      props.$isHover && !props.disabled
        ? theme.colors.grey[100]
        : props.$color};
  }

  &:disabled {
    background-color: ${(props) =>
      props.$isBackground ? theme.colors.disabled.contained : "transparent"};
    color: ${theme.colors.disabled.outlined};
    cursor: not-allowed;
  }

  ${(props) => props.$sx && css({ ...props.$sx })}
`;

const IconWrapper = styled.span<{ $color: string }>`
  margin: 2px;
  display: flex;
  color: ${(props) => props.$color};
`;

const Caption = styled.p`
  width: max-content;
  position: absolute;
  bottom: -20px;
  font-size: 12px;
  color: ${theme.colors.grey[200]};
  text-align: center;
  visibility: hidden;
  ${ButtonStyle}:hover & {
    visibility: visible;
  }
`;

export default IconButton;
