import { theme } from "@/styles/theme";
import { darken } from "@/utils/colorUtils";
import { ReactNode } from "react";
import { styled } from "styled-components";

export type ButtonProps = {
  children: ReactNode;
  $variant?: "text" | "contained" | "outlined";
  $color?: string;
  $backgroundColor?: string;
  disabled?: boolean;
  startIcon?: ReactNode;
  endIcon?: ReactNode;
  onClick?: () => void;
  $fileUrl?: string;
  $fileName?: string;
  $marginTop?: string;
  $marginBottom?: string;
  $marginLeft?: string;
  $marginRight?: string;
  $borderRadius?: string;
  $hasMargin?: boolean;
  $fontFamily?: "kr" | "en";
};
/**---------------------------------------------------------------------------/
  * ! Button 컴포넌트
  * * `children`, `startIcon`, `endIcon`, `disabled`, `onClick`, `$fileUrl`, `$fileName` 지원
  * * `variant`: `text`, `contained`, `outlined` 옵션 제공
  * * 기본 `pointer`에서 `disabled` 상태일 경우 `not-allowed` 스타일 적용
  * * `contained` variant에서 배경색은 `backgroundColor`, 텍스트 색상은 `color`로 설정
  * * `outlined` variant에서 테두리 색상은 `backgroundColor`, 배경은 투명
  * * `text` variant에서 텍스트 색상은 `backgroundColor`, 배경은 투명
  * * `startIcon`, `endIcon`으로 아이콘을 버튼 좌우에 배치할 수 있음
  * * `margin` 관련 스타일 (`$marginTop`, `$marginBottom`, `$marginLeft`, `$marginRight`) 설정 가능
  * * `borderRadius`를 통해 버튼의 모서리 라운드 설정 가능
  * * `fontFamily`: `"kr"` (한글) 또는 `"en"` (영어) 설정 가능
  * * `$fileUrl`과 `$fileName`이 제공되면 버튼 클릭 시 파일 다운로드 처리
  * * `handleDownload` 함수는 `$fileUrl`과 `$fileName`을 이용해 동적으로 파일 다운로드 링크 생성
  * * hover 시, `contained` variant는 `backgroundColor`가 어두워짐 (darken 처리)
  * * `outlined` variant는 hover 시 테두리 색상 변경
/**--------------------------------------------------------------------------*/

const Button = ({
  children,
  $variant = "contained",
  $color = theme.colors.grey[100],
  $backgroundColor = theme.colors.primary.main,
  disabled = false,
  startIcon,
  endIcon,
  onClick,
  $fileUrl,
  $fileName,
  $marginTop,
  $marginBottom,
  $marginLeft,
  $marginRight,
  $borderRadius,
  $hasMargin,
  $fontFamily = "kr",
}: ButtonProps) => {
  const handleDownload = () => {
    if (!$fileUrl || !$fileName) return;

    const link = document.createElement("a");
    link.href = $fileUrl;
    link.setAttribute("download", $fileName);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleClick = () => {
    if ($fileUrl && $fileName) {
      handleDownload();
    } else if (onClick) {
      onClick();
    }
  };

  return (
    <ButtonStyle
      $variant={$variant}
      $color={$color}
      $backgroundColor={$backgroundColor}
      disabled={disabled}
      onClick={handleClick}
      $fileUrl={$fileUrl}
      $fileName={$fileName}
      $marginTop={$marginTop}
      $marginBottom={$marginBottom}
      $marginLeft={$marginLeft}
      $marginRight={$marginRight}
      $borderRadius={$borderRadius}
      $hasMargin={$hasMargin}
    >
      {startIcon && <IconWrapper $color={$color}>{startIcon}</IconWrapper>}
      <TextWrapper
        $hasMargin={!!startIcon || !!endIcon}
        $fontFamily={$fontFamily}
      >
        {children}
      </TextWrapper>
      {endIcon && <IconWrapper $color={$color}>{endIcon}</IconWrapper>}
    </ButtonStyle>
  );
};

const TextWrapper = styled.span<{
  $hasMargin: boolean;
  $fontFamily: "kr" | "en";
}>`
  ${({ $hasMargin }) => $hasMargin && "margin: 0 8px;"}
  font-family: ${(props) =>
    props.$fontFamily === "kr" ? theme.fonts.kr : theme.fonts.en};
`;

export const ButtonStyle = styled.button<ButtonProps>`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 8px 16px;
  font-size: 16px;
  font-weight: bold;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  border: 2px solid transparent;
  margin-top: ${(props) => props.$marginTop || "0"};
  margin-bottom: ${(props) => props.$marginBottom || "0"};
  margin-left: ${(props) => props.$marginLeft || "0"};
  margin-right: ${(props) => props.$marginRight || "0"};
  border-radius: ${(props) => props.$borderRadius || theme.borderRadius[4]};
  background-color: ${(props) =>
    props.$variant === "contained" && !props.disabled
      ? props.$backgroundColor
      : "transparent"};
  color: ${(props) =>
    props.$variant === "contained" && !props.disabled
      ? props.$color
      : props.$variant === "text"
      ? props.$backgroundColor
      : props.$color};
  border: ${(props) =>
    props.$variant === "outlined" && !props.disabled
      ? `2px solid ${props.$backgroundColor}`
      : "none"};
  &:hover {
    background-color: ${(props) =>
      props.$variant === "contained" && !props.disabled
        ? darken(props.$backgroundColor, 0.2)
        : "transparent"};
    border-color: ${(props) =>
      props.$variant === "outlined" && !props.disabled
        ? darken(props.$backgroundColor, 0.2)
        : "transparent"};
  }
  &:disabled {
    background-color: ${(props) =>
      props.$variant === "contained"
        ? theme.colors.disabled.contained
        : "transparent"};
    color: #bdbdbd;
    border: ${(props) =>
      props.$variant === "outlined"
        ? `2px solid ${theme.colors.disabled.outlined}`
        : "none"};
    cursor: not-allowed;
  }
`;

const IconWrapper = styled.span<{ $color: string }>`
  margin: 0 8px;
  display: flex;
  color: ${(props) => props.$color};
`;

export default Button;
