import { keyframes } from "styled-components";

export const TextFlipAnimation = keyframes`
  0% { margin-top: -150px; }
  5% { margin-top: -100px; }
  33% { margin-top: -100px; }
  38% { margin-top: -50px; }
  66% { margin-top: -50px; }
  71% { margin-top: 0px; }
  99.99% { margin-top: 0px; }
  100% { margin-top: -150px; }
`;

export const ProgressBarAnimation = keyframes`
  0% { background-position: 0% 60% }
  50% { background-position: 100% 41% }
  100% { background-position: 0% 60% }
`;
