import { styled } from "styled-components";
import CloudDownloadIcon from "@mui/icons-material/CloudDownload";
import GTranslateIcon from "@mui/icons-material/GTranslate";
import { zIndexHeader } from "@/styles/zIndex";
import { useEffect, useState } from "react";

import { theme } from "@/styles/theme";
import IconButton from "@/components/Button/IconButton";
import { useTranslation } from "react-i18next";
/**---------------------------------------------------------------------------/
  * ! Header Ïª¥Ìè¨ÎÑåÌä∏
  * * ÌéòÏù¥ÏßÄ ÏÉÅÎã®Ïóê Í≥†Ï†ïÎêòÎäî header Î†àÏù¥ÏïÑÏõÉ
  * * Î°úÍ≥†ÏôÄ Îã§Ïö¥Î°úÎìú ÏïÑÏù¥ÏΩòÏùÑ Ìè¨Ìï®
  * * Î°úÍ≥†Îäî `logo_white.png`Î°ú ÏÑ§Ï†ï, ÏïÑÏù¥ÏΩòÏùÄ Material UI `CloudDownloadIcon` ÏÇ¨Ïö©
  * * `position: sticky`Î°ú ÏÉÅÎã®Ïóê Í≥†Ï†ïÎêòÎ©∞, ÌéòÏù¥ÏßÄ Ïä§ÌÅ¨Î°§ ÏãúÏóêÎèÑ ÏÉÅÎã®Ïóê Ïú†ÏßÄ
  * * `height`Îäî 44pxÎ°ú ÏÑ§Ï†ï, ÏñëÏ™Ω Ìå®Îî© 20px
  * * Î∞∞Í≤Ω ÏÉâÏÉÅÏùÄ `rgb(30 30 30 / 80%)`Î°ú Ìà¨Î™ÖÎèÑÎ•º ÏÑ§Ï†ïÌïòÏó¨ Ïñ¥ÎëêÏö¥ Î∞∞Í≤Ω
  * * ÌÖçÏä§Ìä∏ ÏÉâÏÉÅÏùÄ `theme.colors.grey[100]`ÏúºÎ°ú ÏÑ§Ï†ï
  * * `z-index`Îäî `zIndexHeader`Î•º ÏÇ¨Ïö©ÌïòÏó¨ Îã§Î•∏ ÏöîÏÜåÎì§ ÏúÑÏóê ÌëúÏãú
/**--------------------------------------------------------------------------*/

const Header = () => {
  const { i18n } = useTranslation();
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 0);
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleChangeLanguage = (language: "en" | "fr" | "ja" | "ko") => {
    console.log("‚úÖ Ïñ∏Ïñ¥ Î≥ÄÍ≤Ω ÏãúÎèÑ:", language); // üëâ ÌÅ¥Î¶≠ÌñàÏùÑ Îïå Î°úÍ∑∏Í∞Ä Ï∞çÌûàÎäîÏßÄ ÌôïÏù∏
    i18n.changeLanguage(language).then(() => {
      console.log("‚úÖ Ïñ∏Ïñ¥ Î≥ÄÍ≤Ω ÏôÑÎ£å:", i18n.language); // üëâ Î≥ÄÍ≤Ω ÌõÑ Ïã§Ï†ú Ï†ÅÏö©Îêú Ïñ∏Ïñ¥ Ï∂úÎ†•
    });
  };

  return (
    <HeaderStyle $isScrolled={isScrolled}>
      <img src={`/logo_white.png`} alt='Î°úÍ≥†' width='30px' height='auto' />
      <IconButton
        captionText='Ïñ∏Ïñ¥ Î≥ÄÍ≤ΩÌïòÍ∏∞'
        isBackground={false}
        $backgroundColor={theme.colors.background.light}
        onClick={() => handleChangeLanguage("en")}
      >
        <GTranslateIcon />
      </IconButton>
      {/* <CloudDownloadIcon /> */}
    </HeaderStyle>
  );
};
export default Header;

const HeaderStyle = styled.header<{ $isScrolled: boolean }>`
  position: sticky;
  top: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 44px;
  padding: 0 20px;
  color: ${({ theme }) => theme.colors.grey[100]};
  background-color: ${({ $isScrolled }) =>
    $isScrolled ? "rgb(30 30 30 / 80%)" : "transparent"};
  transition: background-color 0.3s ease-in-out;
  z-index: ${zIndexHeader};
`;
