import Box from "@/components/Layout/Box";
import Typography from "@/components/Typography/Typography";
import {
  BLOG_URL,
  GIT_HUB_URL,
  MAIN_MESSAGE_TEXT,
  MAIN_MESSAGE_TITLE,
  MAIN_TEXTS_ANIMATION_ARR,
} from "@/constants/string";
import { TextFlipAnimation } from "@/styles/animation";
import { theme } from "@/styles/theme";
import { styled } from "styled-components";
import gifImage from "@assets/images/emoji.gif";
import IconButton from "@/components/Button/IconButton";
import GitHubIcon from "@mui/icons-material/GitHub";
import WysiwygIcon from "@mui/icons-material/Wysiwyg";
import { useTranslation } from "react-i18next";
import { useEffect, useState } from "react";
/**---------------------------------------------------------------------------/

 * ! MainTextAnimationSection
 * * Î©îÏù∏ ÌéòÏù¥ÏßÄ Î©îÏù∏ Î∂ÄÎ∂Ñ

/**--------------------------------------------------------------------------*/
const MainTextAnimationSection = () => {
  const { t, i18n } = useTranslation();
  const [_, setRender] = useState(false); // ‚úÖ Í∞ïÏ†ú Î¶¨Î†åÎçîÎßÅÏö© ÏÉÅÌÉú Ï∂îÍ∞Ä

  useEffect(() => {
    const onLanguageChanged = () => {
      console.log("üåç Ïñ∏Ïñ¥ Î≥ÄÍ≤Ω Í∞êÏßÄ:", i18n.language);
      setRender((prev) => !prev); // ‚úÖ Ïñ∏Ïñ¥ Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Í∞ïÏ†ú Î¶¨Î†åÎçîÎßÅ
    };

    i18n.on("languageChanged", onLanguageChanged); // ‚úÖ Ïñ∏Ïñ¥ Î≥ÄÍ≤Ω Ïù¥Î≤§Ìä∏ Îì±Î°ù
    return () => {
      i18n.off("languageChanged", onLanguageChanged); // ‚úÖ Ïñ∏Ïñ¥ Î≥ÄÍ≤Ω Ïù¥Î≤§Ìä∏ Ï†úÍ±∞
    };
  }, [i18n]);

  return (
    <Box $height='100%' $alignItems='center' $justifyContent='space-around'>
      <Box $flexDirection='column' $width='auto'>
        <Typography
          $variant='h1'
          $color={theme.colors.grey[100]}
          $marginBottom='10px'
        >
          {t("MAIN_MESSAGE_TITLE")}
        </Typography>
        <FlipWrap>
          <Flip>
            {/* ‚úÖ MAIN_TEXTS_ANIMATION_ARRÎèÑ Î≤àÏó≠ Ï†ÅÏö© */}
            {[0, 1, 2].map((index) => (
              <Typography
                key={index}
                $variant='h1'
                $color={theme.colors.grey[100]}
              >
                {t(`MAIN_TEXTS_ANIMATION_ARR_${index}`)}
              </Typography>
            ))}
          </Flip>
          <Typography
            $variant='h1'
            $color={theme.colors.grey[100]}
            $marginTop='10px'
          >
            {t("MAIN_MESSAGE_TEXT")} {/* ‚úÖ Î≤àÏó≠ Ï†ÅÏö© */}
          </Typography>
        </FlipWrap>
        <Box $marginTop='5%'>
          <IconButton
            href={GIT_HUB_URL}
            captionText={t("GITHUB_BUTTON_TEXT")} // ‚úÖ Î≤àÏó≠ Ï†ÅÏö©
            $backgroundColor={theme.colors.background.light}
          >
            <GitHubIcon />
          </IconButton>
          <IconButton
            href={BLOG_URL}
            captionText={t("BLOG_BUTTON_TEXT")} // ‚úÖ Î≤àÏó≠ Ï†ÅÏö©
            $backgroundColor={theme.colors.background.light}
          >
            <WysiwygIcon />
          </IconButton>
        </Box>
      </Box>
      <img src={gifImage} alt='gifImage' />
    </Box>
  );
};

export default MainTextAnimationSection;

const FlipWrap = styled.ul`
  position: relative;
`;

const Flip = styled.li`
  height: 50px;
  overflow: hidden;
  h1 {
    width: 100%;
    height: 100%;
    line-height: 45px;
  }
  h1:nth-child(1) {
    background-color: ${({ theme }) => theme.colors.primary.dark};
    animation: ${TextFlipAnimation} 5s linear infinite;
  }
  h1:nth-child(2) {
    background-color: ${({ theme }) => theme.colors.primary.light};
  }
  h1:nth-child(3) {
    background-color: ${({ theme }) => theme.colors.primary.main};
  }
`;
