import Box from "@/components/Layout/Box";
import Typography from "@/components/Typography/Typography";
import { useTranslation } from "react-i18next";
import LocalPhoneIcon from "@mui/icons-material/LocalPhone";
import AccessAlarmIcon from "@mui/icons-material/AccessAlarm";
import AlternateEmailIcon from "@mui/icons-material/AlternateEmail";
import { EMAIL_ADDR, PHONE_NUMBER } from "@/constants/string";
import Button from "@/components/Button/Button";
import { theme } from "@/styles/theme";

const ContactUsAside = () => {
  const { t } = useTranslation();
  const handleCall = () => {
    window.location.href = `${PHONE_NUMBER}`;

    const handleCopy = async () => {
      try {
        await navigator.clipboard.writeText(EMAIL_ADDR);
        alert("텍스트가 복사되었습니다!");
      } catch (err) {
        console.error("복사 실패:", err);
      }
    };
  };

  return (
    <Box>
      <Box $alignItems='flex-start' $flexDirection='column'>
        <Typography $variant='h2' $marginBottom='10px'>
          {t("CONTACT_US_SECTION_MESSAGE_TITLE")}
        </Typography>
        <Typography $variant='h6' $color={theme.colors.grey[600]}>
          <AccessAlarmIcon /> {t("CONTACT_US_ASIDE_MESSAGE_TEXT")}
        </Typography>
      </Box>
      <Button $variant='text' startIcon={<LocalPhoneIcon />}>
        {t("CONTACT_US_ASIDE_PHONE_MESSAGE")}
      </Button>

      <Button $variant='text' startIcon={<AlternateEmailIcon />}>
        {t("CONTACT_US_ASIDE_EMAIL_MESSAGE")}
      </Button>
      <Button $variant='text' startIcon={<AccessAlarmIcon />}>
        {t("CONTACT_US_ASIDE_REPONSE_TIME_MESSAGE")}
      </Button>
    </Box>
  );
};
export default ContactUsAside;
