import { ReactNode } from "react";
import { styled } from "styled-components";
import { theme } from "@/styles/theme";

export type ArticleProps = {
  $backgroundColor?: string;
  $height?: string;
  children: ReactNode;
  $flexDirection?: string;
  $borderRadius?: string;
};
/**---------------------------------------------------------------------------/
  * ! Article 컴포넌트
  * * 카드 형식의 레이아웃을 제공하는 섹션 컴포넌트
  * * `$backgroundColor`: 배경 색상 설정, 기본값은 `theme.colors.background.dark`
  * * `$height`: 높이 설정, 기본값은 `"auto"`
  * * `$flexDirection`: `flex` 레이아웃의 방향 설정, 기본값은 `"column"`
  * * `$borderRadius`: 테두리 둥글기 설정, 기본값은 `theme.borderRadius`
  * * `children`: 카드 안에 포함될 내용 (ReactNode)
  * * flex 레이아웃을 사용하여 내부 요소를 배치하며, 필요한 경우 세로 방향으로 레이아웃을 변경할 수 있음
  * * 기본적으로 섹션 안에 카드 형태로 렌더링
/**--------------------------------------------------------------------------*/

const Article = (props: ArticleProps) => {
  const {
    $backgroundColor = theme.colors.background.dark,
    $height = "auto",
    children,
    $flexDirection = "column",
    $borderRadius = theme.borderRadius[20],
  } = props;

  return (
    <ArticleStyle
      $backgroundColor={$backgroundColor}
      $height={$height}
      $flexDirection={$flexDirection}
      $borderRadius={$borderRadius}
    >
      {children}
    </ArticleStyle>
  );
};

export const ArticleStyle = styled.section<{
  $backgroundColor: string;
  $height: string;
  $flexDirection: string;
  $borderRadius: string;
}>`
  width: 100%;
  height: ${(props) => props.$height};
  padding: 20px;
  border-radius: ${(props) => props.$borderRadius};
  display: flex;
  flex-direction: ${(props) => props.$flexDirection};
  padding: 50px 12px 12px 12px;
  background-color: ${(props) => props.$backgroundColor};
`;

export default Article;
