import { theme } from "@/styles/theme";
import { ChangeEvent, FocusEvent, ReactNode, useState } from "react";
import { css, styled } from "styled-components";
import * as yup from "yup";

export type TextFieldProps = {
  label?: string;
  placeholder?: string;
  type?: string;
  value?: string;
  defaultValue?: string;
  disabled?: boolean;
  fullWidth?: boolean;
  error?: boolean;
  helperText?: string;
  startIcon?: ReactNode;
  endIcon?: ReactNode;
  variant?: "outlined" | "filled" | "standard";
  validationSchema?: yup.StringSchema;
  onValidate?: (isValid: boolean, errorMessage?: string) => void;
  onChange?: (e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  onFocus?: (e: FocusEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  onBlur?: (e: FocusEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  $marginTop?: string;
  $marginBottom?: string;
  $marginLeft?: string;
  $marginRight?: string;
  $borderRadius?: string;
  $backgroundColor?: string;
  $sx?: Record<string, any>;
  multiline?: boolean;
  rows?: number;
};

const TextField = ({
  label,
  placeholder,
  type = "text",
  value,
  defaultValue,
  disabled = false,
  fullWidth = false,
  error: externalError = false,
  helperText: externalHelperText = "",
  startIcon,
  endIcon,
  variant = "filled",
  validationSchema,
  onValidate,
  onChange,
  onFocus,
  onBlur,
  $marginTop,
  $marginBottom,
  $marginLeft,
  $marginRight,
  $borderRadius,
  $sx = {},
  multiline = false,
  rows = 3,
  $backgroundColor = theme.colors.white,
}: TextFieldProps) => {
  const [error, setError] = useState<boolean>(externalError);
  const [helperText, setHelperText] = useState<string>(externalHelperText);

  const validate = async (inputValue: string) => {
    if (!validationSchema) return;

    try {
      await validationSchema.validate(inputValue);
      setError(false);
      setHelperText("");
      onValidate?.(true);
    } catch (validationError) {
      const errorMessage = (validationError as yup.ValidationError).message;
      setError(true);
      setHelperText(errorMessage);
      onValidate?.(false, errorMessage);
    }
  };

  const handleChange = (
    e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    if (onChange) onChange(e);
    validate(e.target.value);
  };

  const handleBlur = (
    e: FocusEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    if (onBlur) onBlur(e);
    validate(e.target.value);
  };

  return (
    <InputWrapper
      $fullWidth={fullWidth}
      $marginTop={$marginTop}
      $marginBottom={$marginBottom}
      $marginLeft={$marginLeft}
      $marginRight={$marginRight}
      $sx={$sx}
    >
      {label && <Label $error={error}>{label}</Label>}
      <InputContainer
        $backgroundColor={$backgroundColor}
        $variant={variant}
        $error={error}
        $disabled={disabled}
      >
        {startIcon && <IconWrapper>{startIcon}</IconWrapper>}
        {multiline ? (
          <StyledTextArea
            placeholder={placeholder}
            value={value}
            defaultValue={defaultValue}
            disabled={disabled}
            onChange={handleChange}
            onFocus={onFocus}
            onBlur={handleBlur}
            $variant={variant}
            rows={rows}
            $borderRadius={$borderRadius}
          />
        ) : (
          <StyledInput
            type={type}
            placeholder={placeholder}
            value={value}
            defaultValue={defaultValue}
            disabled={disabled}
            onChange={handleChange}
            onFocus={onFocus}
            onBlur={handleBlur}
            $variant={variant}
            $borderRadius={$borderRadius}
          />
        )}
        {endIcon && <IconWrapper>{endIcon}</IconWrapper>}
      </InputContainer>
      {helperText && <HelperText $error={error}>{helperText}</HelperText>}
    </InputWrapper>
  );
};

// üìå Ï†ÑÏ≤¥ Input ÌïÑÎìú Í∞êÏã∏Îäî Ïª®ÌÖåÏù¥ÎÑà
const InputWrapper = styled.div<{
  $fullWidth: boolean;
  $marginTop?: string;
  $marginBottom?: string;
  $marginLeft?: string;
  $marginRight?: string;
  $sx?: Record<string, any>;
}>`
  display: flex;
  flex-direction: column;
  width: ${(props) => (props.$fullWidth ? "100%" : "auto")};
  margin-top: ${(props) => props.$marginTop || "0"};
  margin-bottom: ${(props) => props.$marginBottom || "0"};
  margin-left: ${(props) => props.$marginLeft || "0"};
  margin-right: ${(props) => props.$marginRight || "0"};
  ${(props) => props.$sx && css({ ...props.$sx })}
`;

// üìå ÎùºÎ≤®
const Label = styled.label<{ $error: boolean }>`
  font-size: 14px;
  font-weight: 500;
  color: ${(props) =>
    props.$error ? theme.colors.validation.error : theme.colors.grey[100]};
  margin-bottom: 4px;
`;

// üìå ÏûÖÎ†• ÌïÑÎìú Ïª®ÌÖåÏù¥ÎÑà
const InputContainer = styled.div.withConfig({
  shouldForwardProp: (prop) => prop !== "error" && prop !== "fullWidth",
})<{
  $variant: string;
  $error: boolean;
  $disabled: boolean;
  $backgroundColor: string;
}>`
  display: flex;
  align-items: center;
  border: ${(props) =>
    props.$variant === "outlined"
      ? `2px solid ${
          props.$error ? theme.colors.validation.error : theme.colors.grey[400]
        }`
      : "none"};
  background-color: ${(props) =>
    props.$variant === "filled"
      ? props.$disabled
        ? theme.colors.grey[300]
        : props.$backgroundColor || theme.colors.grey[100]
      : "transparent"};
  border-bottom: ${(props) =>
    props.$variant === "standard"
      ? `2px solid ${
          props.$error ? theme.colors.validation.error : theme.colors.grey[400]
        }`
      : "none"};
  padding: 8px 12px;
  border-radius: ${theme.borderRadius[4]};
  transition: 0.3s ease-in-out;
`;

// üìå ÏûÖÎ†• ÌïÑÎìú (TextArea Ï∂îÍ∞Ä)
const StyledInput = styled.input<{ $variant: string; $borderRadius?: string }>`
  flex: 1;
  border: none;
  outline: none;
  background: transparent;
  font-size: 16px;
  padding: 8px;
  border-radius: ${(props) => props.$borderRadius || theme.borderRadius[4]};
`;

// üìå Î©ÄÌã∞ÎùºÏù∏ ÏûÖÎ†• ÌïÑÎìú
const StyledTextArea = styled.textarea<{
  $variant: string;
  $borderRadius?: string;
}>`
  flex: 1;
  border: none;
  outline: none;
  background: transparent;
  font-size: 16px;
  padding: 8px;
  border-radius: ${(props) => props.$borderRadius || theme.borderRadius[4]};
  resize: none; // To prevent resizing
`;

// üìå ÏïÑÏù¥ÏΩò ÎûòÌçº
const IconWrapper = styled.span`
  margin: 0 8px;
  display: flex;
  align-items: center;
`;

// üìå Ìó¨Ìçº ÌÖçÏä§Ìä∏
const HelperText = styled.span<{ $error: boolean }>`
  font-size: 12px;
  margin-top: 4px;
  color: ${(props) =>
    props.$error ? theme.colors.validation.error : theme.colors.grey[600]};
`;

export default TextField;
