import React from "react";
import styled from "styled-components";
import { Swiper, SwiperProps, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.css";

// CardProps 타입 정의
export type CardProps = SwiperProps & {
  items: React.ReactNode[]; // items로 ReactNode 배열을 받음
  spaceBetween?: number; // 추가적인 Swiper 옵션 (슬라이드 간격 등)
  slidesPerView?: number; // 한 번에 보여줄 슬라이드 개수
};

const Slider = (props: CardProps) => {
  const { items, spaceBetween = 50, slidesPerView = 2 } = props;
  return (
    <SliderContainer>
      <Swiper
        spaceBetween={spaceBetween}
        slidesPerView={slidesPerView}
        {...props}
      >
        {/* items 배열을 map하여 각 항목을 SwiperSlide로 출력 */}
        {items.map((item, index) => (
          <SwiperSlide key={index}>
            <SlideItem>{item}</SlideItem>
          </SwiperSlide>
        ))}
      </Swiper>
    </SliderContainer>
  );
};

const SliderContainer = styled.div`
  width: 100%;
  max-width: 800px;
  margin: 0 auto;
`;

const SlideItem = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  /* background-color: #eee;
  border-radius: 8px;
  padding: 20px;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px; */
`;

export default Slider;
