import { useRef, useEffect, ReactNode } from "react";
import { styled } from "styled-components";

// Popover 컴포넌트
interface PopoverProps {
  anchorEl: HTMLElement | null;
  open: boolean;
  onClose: () => void;
  children: ReactNode;
  anchorOrigin?: { vertical: "top" | "bottom"; horizontal: "left" | "right" };
  transformOrigin?: {
    vertical: "top" | "bottom";
    horizontal: "left" | "right";
  };
}

const Popover = (props: PopoverProps) => {
  const {
    anchorEl,
    open,
    onClose,
    children,
    anchorOrigin = { vertical: "bottom", horizontal: "right" },
    transformOrigin = { vertical: "top", horizontal: "right" },
  } = props;

  const popoverRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        popoverRef.current &&
        !popoverRef.current.contains(event.target as Node)
      ) {
        onClose();
      }
    };
    if (open) {
      document.addEventListener("mousedown", handleClickOutside);
    }
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [open, onClose]);

  if (!open || !anchorEl) return null;

  const { bottom, left, width, height } = anchorEl.getBoundingClientRect();

  // anchorOrigin 및 transformOrigin을 기준으로 top, left 값을 설정
  const anchorVertical = anchorOrigin.vertical;
  const anchorHorizontal = anchorOrigin.horizontal;

  // `top`은 anchorOrigin을 기준으로 계산됨
  const popoverTop = anchorVertical === "bottom" ? bottom : bottom - height;

  // `left`와 `right`는 anchorHorizontal을 기준으로 설정
  const popoverLeft = anchorHorizontal === "left" ? 0 : undefined;
  const popoverRight = anchorHorizontal === "right" ? 0 : undefined;

  return (
    <PopoverContainer
      ref={popoverRef}
      $top={popoverTop}
      $popoverLeft={popoverLeft}
      $popoverRight={popoverRight}
    >
      {children}
    </PopoverContainer>
  );
};

export default Popover;

// 스타일링
const PopoverContainer = styled.div<{
  $top: number;
  $popoverLeft?: number;
  $popoverRight?: number;
}>`
  position: absolute;
  top: ${({ $top }) => $top}px;
  left: ${({ $popoverLeft }) =>
    $popoverLeft !== undefined ? `${$popoverLeft}px` : "auto"};
  right: ${({ $popoverRight }) =>
    $popoverRight !== undefined ? `${$popoverRight}px` : "auto"};
  background-color: ${({ theme }) => theme.colors.background.light};
  padding: 10px;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
  min-width: 150px;
  z-index: 999;
`;
