import { styled } from "styled-components";
import CloudDownloadIcon from "@mui/icons-material/CloudDownload";
import GTranslateIcon from "@mui/icons-material/GTranslate";
import { zIndexHeader } from "@/styles/zIndex";
import { useEffect, useState } from "react";

import { theme } from "@/styles/theme";
import IconButton from "@/components/Button/IconButton";
import { useTranslation } from "react-i18next";
/**---------------------------------------------------------------------------/
  * ! Header 컴포넌트
  * * 페이지 상단에 고정되는 header 레이아웃
  * * 로고와 다운로드 아이콘을 포함
  * * 로고는 `logo_white.png`로 설정, 아이콘은 Material UI `CloudDownloadIcon` 사용
  * * `position: sticky`로 상단에 고정되며, 페이지 스크롤 시에도 상단에 유지
  * * `height`는 44px로 설정, 양쪽 패딩 20px
  * * 배경 색상은 `rgb(30 30 30 / 80%)`로 투명도를 설정하여 어두운 배경
  * * 텍스트 색상은 `theme.colors.grey[100]`으로 설정
  * * `z-index`는 `zIndexHeader`를 사용하여 다른 요소들 위에 표시
/**--------------------------------------------------------------------------*/

const Header = () => {
  const { i18n } = useTranslation();
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 0);
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleChangeLanguage = (language: "en" | "fr" | "ja" | "ko") => {
    i18n.changeLanguage(language).then(() => {
      console.log("✅ 언어 변경 완료:", language);
    });
  };

  return (
    <HeaderStyle $isScrolled={isScrolled}>
      <img src={`/logo_white.png`} alt='로고' width='30px' height='auto' />
      <IconButton
        captionText='언어 변경하기'
        isBackground={false}
        $backgroundColor={theme.colors.background.light}
        onClick={() => handleChangeLanguage("en")}
      >
        <GTranslateIcon />
      </IconButton>
      {/* <CloudDownloadIcon /> */}
    </HeaderStyle>
  );
};
export default Header;

const HeaderStyle = styled.header<{ $isScrolled: boolean }>`
  position: sticky;
  top: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 44px;
  padding: 0 20px;
  color: ${({ theme }) => theme.colors.grey[100]};
  background-color: ${({ $isScrolled }) =>
    $isScrolled ? "rgb(30 30 30 / 80%)" : "transparent"};
  transition: background-color 0.3s ease-in-out;
  z-index: ${zIndexHeader};
`;
