import { theme } from "@/styles/theme";
import { ReactNode, useEffect, useState } from "react";
import { css, styled } from "styled-components";
import ErrorOutlineIcon from "@mui/icons-material/ErrorOutline";
import WarningAmberIcon from "@mui/icons-material/WarningAmber";
import InfoIcon from "@mui/icons-material/Info";
import CheckCircleOutlineIcon from "@mui/icons-material/CheckCircleOutline";
import IconButton from "@/components/Button/IconButton";
import HighlightOffIcon from "@mui/icons-material/HighlightOff";
import Typography from "@/components/Typography/Typography";

export type AlertProps = {
  children: ReactNode;
  severity?: "error" | "warning" | "info" | "success";
  $backgroundColor?: string;
  $color?: string;
  $iconColor?: string;
  $borderRadius?: string;
  $marginTop?: string;
  $marginBottom?: string;
  $paddingTop?: string;
  $paddingBottom?: string;
  $paddingLeft?: string;
  $paddingRight?: string;
  $sx?: Record<string, any>;
  onClose?: () => void;
  autoCloseDuration?: number;
};

/**---------------------------------------------------------------------------/
  * ! Alert 컴포넌트
  * * `severity`: `error`, `warning`, `info`, `success`의 상태를 표현
  * * `children`: Alert 안에 보여줄 텍스트 또는 아이콘 등
  * * `onClose`: Alert이 닫힐 때 실행할 함수 (닫기 버튼 클릭 시)
  * * `autoCloseDuration`: Alert이 자동으로 사라지는 시간 (기본 5000ms)
  * * 기본적으로 배경색, 텍스트 색, 아이콘 색을 `severity`에 맞게 다르게 설정
/**--------------------------------------------------------------------------*/

const Alert = ({
  children,
  severity = "info",
  $backgroundColor,
  $color,
  $iconColor,
  $borderRadius = theme.borderRadius[4],
  $marginTop = "0",
  $marginBottom = "0",
  $paddingTop = "8px",
  $paddingBottom = "8px",
  $paddingLeft = "16px",
  $paddingRight = "16px",
  $sx = {},
  onClose,
  autoCloseDuration = 5000,
}: AlertProps) => {
  const [isClosed, setIsClosed] = useState(false);

  const severityStyles = {
    error: {
      backgroundColor: $backgroundColor || theme.colors.validation.error,
      color: $color || theme.colors.white,
      iconColor: $iconColor || theme.colors.white,
    },
    warning: {
      backgroundColor: $backgroundColor || theme.colors.validation.warning,
      color: $color || theme.colors.white,
      iconColor: $iconColor || theme.colors.white,
    },
    info: {
      backgroundColor: $backgroundColor || theme.colors.validation.info,
      color: $color || theme.colors.white,
      iconColor: $iconColor || theme.colors.white,
    },
    success: {
      backgroundColor: $backgroundColor || theme.colors.validation.success,
      color: $color || theme.colors.white,
      iconColor: $iconColor || theme.colors.white,
    },
  };

  useEffect(() => {
    if (!onClose) {
      const timer = setTimeout(() => {
        setIsClosed(true);
      }, autoCloseDuration);

      return () => clearTimeout(timer);
    }
  }, [onClose, autoCloseDuration]);

  if (isClosed) return null;

  return (
    <AlertStyle
      $backgroundColor={severityStyles[severity].backgroundColor}
      $color={severityStyles[severity].color}
      $iconColor={severityStyles[severity].iconColor}
      $borderRadius={$borderRadius}
      $marginTop={$marginTop}
      $marginBottom={$marginBottom}
      $paddingTop={$paddingTop}
      $paddingBottom={$paddingBottom}
      $paddingLeft={$paddingLeft}
      $paddingRight={$paddingRight}
      $sx={$sx}
    >
      <IconWrapper $iconColor={severityStyles[severity].iconColor}>
        {severity === "error" && <ErrorOutlineIcon />}
        {severity === "warning" && <WarningAmberIcon />}
        {severity === "info" && <InfoIcon />}
        {severity === "success" && <CheckCircleOutlineIcon />}
      </IconWrapper>
      <Typography $variant='h6'>{children}</Typography>

      {onClose && (
        <IconButton
          onClick={onClose}
          isBackground={false}
          isHover={false}
          $sx={{ padding: 0 }}
        >
          <HighlightOffIcon />
        </IconButton>
      )}
    </AlertStyle>
  );
};

const AlertStyle = styled.div<AlertProps>`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: ${(props) => props.$backgroundColor};
  color: ${(props) => props.$color};
  border-radius: ${(props) => props.$borderRadius};
  margin-top: ${(props) => props.$marginTop};
  margin-bottom: ${(props) => props.$marginBottom};
  padding-top: ${(props) => props.$paddingTop};
  padding-bottom: ${(props) => props.$paddingBottom};
  padding-left: ${(props) => props.$paddingLeft};
  padding-right: ${(props) => props.$paddingRight};
  ${(props) => props.$sx && css({ ...props.$sx })}
`;

const IconWrapper = styled.span<{ $iconColor: string }>`
  margin-right: 12px;
  font-size: 24px;
  color: ${(props) => props.$iconColor};
`;

export default Alert;
