import Box from "@/components/Layout/Box";
import Typography from "@/components/Typography/Typography";
import { useTranslation } from "react-i18next";
import LocalPhoneIcon from "@mui/icons-material/LocalPhone";
import AlternateEmailIcon from "@mui/icons-material/AlternateEmail";
import { EMAIL_ADDR, PHONE_NUMBER } from "@/constants/string";
import Button from "@/components/Button/Button";
import { theme } from "@/styles/theme";
import Alert from "@/components/Feedback/Alert";
import { useState } from "react";

const ContactUsAside = () => {
  const { t } = useTranslation();
  const [alerts, setAlerts] = useState<{ id: string; message: string }[]>([]); // 상태를 배열로 변경

  const handleCall = () => {
    window.location.href = `${PHONE_NUMBER}`;
  };

  const handleCopy = async () => {
    const id = new Date().getTime().toString(); // 각 알림에 고유한 ID 생성
    try {
      await navigator.clipboard.writeText(EMAIL_ADDR);
      setAlerts((prevAlerts) => [
        ...prevAlerts,
        { id, message: t("SUCCESS_INVALID_TEXT_COPY") },
      ]);
    } catch (err) {
      setAlerts((prevAlerts) => [
        ...prevAlerts,
        { id, message: `${t("SUCCESS_INVALID_TEXT_COPY")} ${err}` },
      ]);
    }
  };

  // 알림 자동으로 제거
  const removeAlert = (id: string) => {
    setAlerts((prevAlerts) => prevAlerts.filter((alert) => alert.id !== id));
  };

  return (
    <Box $alignItems='center' $justifyContent='space-around'>
      <div>
        <Typography $variant='h2' $marginBottom='10px'>
          {t("CONTACT_US_ASIDE_MESSAGE_TITLE")}
        </Typography>
        <Typography $variant='h6' $color={theme.colors.grey[600]}>
          {t("CONTACT_US_ASIDE_MESSAGE_TEXT")}
        </Typography>
      </div>
      <div>
        <Button
          onClick={handleCall}
          $variant='text'
          startIcon={<LocalPhoneIcon />}
        >
          {t("CONTACT_US_ASIDE_PHONE_MESSAGE")}
        </Button>
        <Button
          onClick={handleCopy}
          $variant='text'
          startIcon={<AlternateEmailIcon />}
        >
          {t("CONTACT_US_ASIDE_EMAIL_MESSAGE")}
        </Button>
      </div>
      {alerts.map(({ id, message }) => (
        <Alert
          key={id}
          onClose={() => removeAlert(id)}
          autoCloseDuration={1000} // 5초 후 자동으로 사라짐
        >
          {message}
        </Alert>
      ))}
    </Box>
  );
};

export default ContactUsAside;
