import { ReactNode } from "react";
import styled from "styled-components";
import { Swiper, SwiperProps, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.css";
import { Navigation } from "swiper/modules";
import "swiper/css/navigation";

// CardProps 타입 정의
export type CardProps = SwiperProps & {
  items: ReactNode[]; // items로 ReactNode 배열을 받음
  spaceBetween?: number; // 추가적인 Swiper 옵션 (슬라이드 간격 등)
  slidesPerView?: number; // 한 번에 보여줄 슬라이드 개수
  centeredSlides?: boolean;
  freeMode?: boolean;
  sx?: Record<string, any>; // $sx → sx로 변경 (styled-components에서 안전하게 처리)
};

const Slider = ({ items, sx = {}, ...props }: CardProps) => {
  return (
    <SliderContainer style={sx}>
      <Swiper navigation={true} modules={[Navigation]} {...props}>
        {items.map((item, index) => (
          <SwiperSlide key={index}>{item}</SwiperSlide>
        ))}
      </Swiper>
    </SliderContainer>
  );
};

// ✅ Styled-Components에서 불필요한 props 제거
const SliderContainer = styled.div.attrs(() => ({
  // 여기서 특정 HTML 속성을 제거할 수도 있음
}))`
  width: 100%;
  .swiper-slide {
    display: flex;
    flex-direction: column;
    justify-content: center;
  }
  .swiper-wrapper {
    margin-inline-start: calc(-268.46154px + 28.36538vw);
  }
`;

export default Slider;
