import { ProgressAnimation } from "@/styles/animation";
import { theme } from "@/styles/theme";
import styled from "styled-components";

export type ProgressProps = {
  value: number;
  variant?: "bar" | "circular";
  size?: number;
  barWidth?: string;
  height?: string;
  color?: string;
};

const Progress = (props: ProgressProps) => {
  const {
    value,
    variant = "bar",
    size = 100,
    barWidth = "100%",
    height = "8px",
    color = theme.colors.primary.main,
  } = props;
  return variant === "bar" ? (
    <BarContainer width={barWidth} height={height}>
      <BarFill value={value} color={color} />
    </BarContainer>
  ) : (
    <CircularContainer size={size}>
      <svg viewBox='0 0 100 100'>
        <circle className='bg' cx='50' cy='50' r='45' />
        <circle
          className='progress'
          cx='50'
          cy='50'
          r='45'
          strokeDasharray='283'
          strokeDashoffset={283 - (value / 100) * 283}
          style={{ stroke: color }}
        />
        <text x='50%' y='50%' dy='.3em' textAnchor='middle'>
          {value}%
        </text>
      </svg>
    </CircularContainer>
  );
};

const BarContainer = styled.div<{ width: string; height: string }>`
  width: ${(props) => props.width};
  height: ${(props) => props.height};
  background: #e5e9eb;
  border-radius: ${theme.borderRadius[4]};
  overflow: hidden;
  position: relative;
`;

const BarFill = styled.div<{ value: number; color: string }>`
  height: 100%;
  width: ${(props) => props.value}%;
  background: ${(props) => props.color};
  border-radius: ${theme.borderRadius[4]};
  transition: width 1.5s cubic-bezier(0.8, -0.07, 1, 1);
  animation: ${ProgressAnimation} 1.5s ease-in-out;
`;

const CircularContainer = styled.div<{ size: number }>`
  width: ${(props) => props.size}px;
  height: ${(props) => props.size}px;
  position: relative;

  svg {
    transform: rotate(-90deg);
    width: 100%;
    height: 100%;
  }

  circle {
    fill: none;
    stroke-width: 10;
  }

  .bg {
    stroke: #e5e9eb;
  }

  .progress {
    stroke-linecap: round;
    transition: stroke-dashoffset 1.5s ease-in-out;
  }

  text {
    font-size: 18px;
    font-weight: bold;
    fill: #333;
  }
`;

export default Progress;
