import { useState, useEffect } from "react";
import { styled } from "styled-components";
import { theme } from "@/styles/theme";
import IconButton from "@/components/Button/IconButton";
import { useTranslation } from "react-i18next";
import Popover from "@/components/util/Popover";
import Button from "@/components/Button/Button";
import GTranslateIcon from "@mui/icons-material/GTranslate";
import { zIndexHeader } from "@/styles/zIndex";

const Header = () => {
  const { i18n } = useTranslation();
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 0);
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const handleChangeLanguage = (language: "en" | "fr" | "ja" | "ko") => {
    i18n.changeLanguage(language).then(() => {});
  };

  const [open, setOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState<HTMLElement | null>(null);

  // `event`를 받아서 처리하는 방법
  const handlePopoverOpen = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget); // event.currentTarget으로 anchorEl 설정
    setOpen(true);
  };

  const handlePopoverClose = () => {
    setOpen(false);
  };

  return (
    <HeaderStyle $isScrolled={isScrolled}>
      <img src={`/logo_white.png`} alt='로고' width='30px' height='auto' />
      <IconButton
        captionText='언어 변경하기'
        isBackground={false}
        $backgroundColor={theme.colors.background.light}
        onClick={handlePopoverOpen}
      >
        <GTranslateIcon />
      </IconButton>
      <Popover anchorEl={anchorEl} open={open} onClose={handlePopoverClose}>
        <Button onClick={() => handleChangeLanguage("en")}>en</Button>
        <div>이것은 Popover 내용입니다.</div>
        <div>여기에 다른 내용도 추가할 수 있습니다.</div>
      </Popover>
    </HeaderStyle>
  );
};

export default Header;

const HeaderStyle = styled.header<{ $isScrolled: boolean }>`
  position: sticky;
  top: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 44px;
  padding: 0 20px;
  color: ${({ theme }) => theme.colors.grey[100]};
  background-color: ${({ $isScrolled }) =>
    $isScrolled ? "rgb(30 30 30 / 80%)" : "transparent"};
  transition: background-color 0.3s ease-in-out;
  z-index: ${zIndexHeader};
`;
